import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

export const AppointmentForm = () => {

    const initialFormState = {
        docid: 1,
        patid: 1,
        date: '',
        time: '',
        service: ''
    };

    const [appointment, setAppointment] = useState(initialFormState);
    const navigate = useNavigate();
    const { id } = useParams;


    useEffect(() => {
        if ( id !== 'new') {
            const baseUrl: string = "http://localhost:8080/appointments";
            const url: string = `${baseUrl}/${id}`;
            fetch(url)
            .then(response => response.json())
            .then(data => setAppointment(data));
        }

    }, [id, setAppointment]);

    return (

        <div className='text-white' id='container' style={{ backgroundColor: "#202124", textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center", height: "82vh" }}>

            <div style={{ width: "30%" }}>

                <p className="h4 mb-4">Add (or update) Appointment</p>

                <hr />

                <form action="/save}" method="POST" >

                    <input type="hidden" name="appid" />

                        <div className="form-floating">
                            <input type="text" name="docid" id="titleText" className="form-control md-4" />
                            <label className="form-label" htmlFor="titleText">Doctor</label>
                        </div>

                    <br />
                        <div className="form-floating">
                            <input type="text" name="patid" id="authorText" className="form-control md-4" />
                            <label className="form-label" htmlFor="authorText">Patient</label>
                        </div>
                    <br />
                        <div className="form-floating">
                            <input type="text" name="date" id="isbnText" className="form-control md-4" />
                            <label className="form-label" htmlFor="isbnText">Date</label>
                        </div>
                    <br />
                        <div className="form-floating">
                            <input type="text" name="time" id="priceText" className="form-control md-4" />
                            <label className="form-label" htmlFor="priceText">Time</label>
                        </div>
                    <br />
                        <div className="form-floating">
                            <input type="text" name="service" id="serviceText" className="form-control md-4" />
                            <label className="form-label" htmlFor="serviceText">Service</label>
                        </div>
                    <br></br>

                    <button type="submit" className="btn btn-info col-2">Save</button>

                </form>

            </div>
        </div>
    );
}
