import { useEffect, useState } from "react";
import AppointmentModel from "./AppointmentModel";

export const Appointment = () => {
    const [appointments, setAppointments] = useState<AppointmentModel[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [httpError, setHttpError] = useState(null);

    useEffect(() => {
        const fetchAppointments = async () => {
            const baseUrl: string = "http://localhost:8080/appointments";
            const url: string = `${baseUrl}?page=0size=9`;
            const response = await fetch(url);
            if (!response.ok) {
                throw new Error('Something went wrong!');
            }
            const responseJson = await response.json();
            const responseData = responseJson._embedded.appointments;
            const loadedAppointments: AppointmentModel[] = [];
            for (const key in responseData) {
                loadedAppointments.push({
                    appid: responseData[key].appid,
                    docid: responseData[key].docid,
                    patid: responseData[key].patid,
                    date: responseData[key].date,
                    time: responseData[key].time,
                    service: responseData[key].service,
                });
            }
            setAppointments(loadedAppointments);
            setIsLoading(false);
        };

        fetchAppointments().catch((error: any) => {
            setIsLoading(false);
            setHttpError(error.message);
        })
    }, []);


    return (
        <div className='text-white' id='container' style={{ backgroundColor: "#202124", textAlign: "center", display: "flex", justifyContent: "center", alignItems: "center", height: "82vh" }}>

            <div><h3>Appointments</h3></div>
            <hr />
            <table className="table table-striped">
                <thead className="table-dark">
                    <tr>
                        <th>#</th>
                        <th>Doctor</th>
                        <th>Patient</th>
                        <th>Date</th>
                        <th>Time</th>
                        <th>Service</th>
                        <th style={{ textAlign: "center" }}>Action</th>
                    </tr>
                </thead>
                <tbody className="table-group-divider table-divider-color">

	                        <tr>
                            <th scope='row'>1</th>
                            <td>1</td>
                            <td>2</td>
                            <td>8/20/2024</td>
                            <td>10:00</td>
                            <td>Family</td>
                            <td><button className='btn btn-danger'>Delete</button>&nbsp;
                                <button className='btn btn-success'>Edit</button></td>
                        </tr>
                </tbody>
            </table>
        </div>
    );

}
